  //     },
  //   });
  //   const json = await response.json();
  //   console.log(json);
  // };

  // useEffect(() => {
  //   checkSession();
  // }, []);

  const isLogged = () => !!user;

  const contextValue = {
    user,
    setUser,
    isLogged,
  };

  return (
    <AuthContext.Provider value={contextValue}>{children}</AuthContext.Provider>
  );
};

export default AuthProvider;
