import React, { useEffect, useState } from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import { useRouteMatch } from "react-router";
import { Link } from "react-router-dom";
import useAuth from "../../auth/useAuth";
import styles from "./AccountPage.module.css";

const AccountPageCita = ({ children }) => {
  const { user } = useAuth();
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [sidebar, setSidebar] = useState(true);

  const { path, url } = useRouteMatch();

  const showSidebar = () => setSidebar(!sidebar);

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    // console.log(json);
    setAppointment(json);
    setHasLoaded(true);
    // console.log(json);
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  return (
    <Container fluid>
      <h1
        className={
          sidebar
            ? `col-md-8 ml-sm-auto col-lg-9 col-xl-10 p-0 ${styles.title}`
            : `${styles.title} col-12 p-0`
        }
      >
        Citas
      </h1>
      <Row
        className={
          sidebar
            ? `col-md-8 ml-sm-auto col-lg-9 col-xl-10 p-0 ${styles.infoContainer}`
            : `${styles.infoContainer} col-12 p-0`
        }
      >
        {hasLoaded ? (
          Object.keys(appointment).map((i, cont) => {
            return (
              <Col
                lg={3}
                md={4}
                className="p-2"
                key={appointment[i].appointment_id}
              >
                <Card>
                  <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
                  <Card.Body>
                    <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                    <Card.Text>{appointment[i].descripcion}</Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            );
          })
        ) : (
          <p>asdasd</p>
        )}
      </Row>
    </Container>
  );
};

export default AccountPageCita;
