import React, { useContext, useEffect, useState } from "react";
import { Card, Col, Container, Row } from "react-bootstrap";
import { SidebarContext } from "../../components/BaseAccountPage";
import styles from "./AccountPage.module.css";

const CitaAccountPage = () => {
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();

  const { sidebar, setSidebar } = useContext(SidebarContext);

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    setAppointment(json);
    setHasLoaded(true);
  };

  useEffect(() => {
    fetchAppointments();
    setSidebar(true);
  }, [setSidebar]);

  return (
    <Container fluid>
      <h1
        className={
          sidebar
            ? `col-md-8 ml-sm-auto col-lg-9 col-xl-10 p-0 ${styles.title}`
            : `${styles.title} col-12 p-0`
        }
      >
        Citas
      </h1>
      <Row
        className={
          sidebar
            ? `col-md-9 ml-sm-auto col-lg-10 col-xl-10 p-0 ${styles.infoContainer}`
            : `${styles.infoContainer} col-12 p-0`
        }
      >
        {hasLoaded ? (
          Object.keys(appointment).map((i, cont) => {
            return (
              <Col
                lg={3}
                md={4}
                className="p-2"
                key={appointment[i].appointment_id}
              >
                <CardDeck>
                  <Card>
                    <Card.Img variant="top" src="holder.js/100px160" />
                    <Card.Body>
                      <Card.Title>Card title</Card.Title>
                      <Card.Text>
                        This is a wider card with supporting text below as a
                        natural lead-in to additional content. This content is a
                        little bit longer.
                      </Card.Text>
                    </Card.Body>
                    <Card.Footer>
                      <small className="text-muted">
                        Last updated 3 mins ago
                      </small>
                    </Card.Footer>
                  </Card>
                  <Card>
                    <Card.Img variant="top" src="holder.js/100px160" />
                    <Card.Body>
                      <Card.Title>Card title</Card.Title>
                      <Card.Text>
                        This card has supporting text below as a natural lead-in
                        to additional content.{" "}
                      </Card.Text>
                    </Card.Body>
                    <Card.Footer>
                      <small className="text-muted">
                        Last updated 3 mins ago
                      </small>
                    </Card.Footer>
                  </Card>
                  <Card>
                    <Card.Img variant="top" src="holder.js/100px160" />
                    <Card.Body>
                      <Card.Title>Card title</Card.Title>
                      <Card.Text>
                        This is a wider card with supporting text below as a
                        natural lead-in to additional content. This card has
                        even longer content than the first to show that equal
                        height action.
                      </Card.Text>
                    </Card.Body>
                    <Card.Footer>
                      <small className="text-muted">
                        Last updated 3 mins ago
                      </small>
                    </Card.Footer>
                  </Card>
                </CardDeck>
              </Col>
            );
          })
        ) : (
          <p>asdasd</p>
        )}
      </Row>
    </Container>
  );
};

export default CitaAccountPage;
