import React, { useEffect, useRef } from "react";
import { Button, Container, Form } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import { APIUsers } from "../../api/api";
import useAuth from "../../auth/useAuth";
import styles from "./Login.module.css";

const LoginPage = () => {
  const { user, setUser } = useAuth();
  const fieldsMap = useRef(new Map());

  let history = useHistory();

  const redirect = () => {
    history.push("/registro");
  };

  useEffect(() => {
    if (user) redirect();
  }, []);

  const handleSubmit = async (e) => {
    console.log(fieldsMap);
    const data = {
      nombre: fieldsMap.current.get("formEmail"),
    };
    console.log(data);
    e.preventDefault();
    await APIUsers.createUser({
      user: "user",
      password: "password",
    });
  };

  return (
    <Container className={styles.container}>
      <h1 className={styles.title}>Iniciar Sesión</h1>
      <Form className={styles.form}>
        <Form.Group controlId="formEmail">
          <Form.Label>Correo Electrónico</Form.Label>
          <Form.Control type="email" name="email" ref={fieldsMap} />
        </Form.Group>
        <Form.Group controlId="formPassword">
          <Form.Label>Contraseña</Form.Label>
          <Form.Control type="password" name="password" ref={fieldsMap} />
        </Form.Group>
        <Button
          variant="success"
          className={styles.submitBtn}
          onClick={handleSubmit}
        >
          Iniciar Sesión
        </Button>
      </Form>
      <Container className={styles.redirect}>
        ¿Aún no tienes cuenta? <Link to="/registro">Registrate</Link>
      </Container>
    </Container>
  );
};

export default LoginPage;
