import React, { useEffect, useState } from "react";
import { Card } from "react-bootstrap";
import { Link } from "react-router-dom";
import styles from "./AccountPage.module.css";
// import Navi from "./Navi";

const AccountPage = () => {
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [sidebar, setSidebar] = useState(true);

  const showSidebar = () => setSidebar(!sidebar);

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    // console.log(json);
    setAppointment(json);
    setHasLoaded(true);
    // console.log(json);
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  return (
    <>
      <div className={styles.navbar}>
        <Link to="#" className={styles.menuBars}>
          <box-icon
            name="cart"
            type="solid"
            size="md"
            onClick={showSidebar}
          ></box-icon>
        </Link>
      </div>
      <nav
        className={sidebar ? `${styles.navMenu} ${styles.active}` : "nav-menu"}
      >
        <ul className="nav-menu-items" onClick={showSidebar}>
          <li className="navbar-toggle">
            <Link to="/" className="menu-bars">
              {/* <AiIcons.AiOutlineClose /> */}
              <box-icon name="cart" type="solid" size="md"></box-icon>
            </Link>
          </li>
          <li className="nav-text">asdasdasd</li>
          <li className="nav-text">asdasd</li>
        </ul>
      </nav>
      <div className={sidebar ? "col-md-9 ml-sm-auto col-lg-10 px-md-4" : ""}>
        {hasLoaded ? (
          Object.keys(appointment).map((i, cont) => {
            return (
              <Card key={appointment[i].appointment_id}>
                <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
                <Card.Body>
                  <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                  <Card.Text>{appointment[i].descripcion}</Card.Text>
                </Card.Body>
              </Card>
            );
          })
        ) : (
          <p>asdasd</p>
        )}
      </div>
    </>
  );
};

export default AccountPage;
