import { useEffect, useState } from "react";
import { Alert } from "react-bootstrap";

const Alerts = (message, variant) => {
  const [showAlert, setShowAlert] = useState();

  useEffect(() => {
    showAlert(true);
    setTimeout(() => {
      setShowAlert(false);
    }, 3000);
  }, [showAlert]);

  return <Alert variant={variant}>{message}</Alert>;
};

export { Alerts };

