import { useContext } from "react";
import { Navbar, Nav } from "react-bootstrap";
import { Link } from "react-router-dom";
import useAuth from "../auth/useAuth";
import { CartContext } from "../layouts/Layout";
import styles from "./Navigation.module.css";

const Navigation = () => {
  const { user, setUser } = useAuth();
  const { cart } = useContext(CartContext);
  const { clearCartInputs } = useContext(CartContext);

  const handleLogOut = async (e) => {
    e.preventDefault();
    const response = await fetch("/auth/logout");
    const json = await response.json();
    console.log(json);
    setUser("");
    clearCartInputs();
  };

  return (
    <Navbar
      collapseOnSelect
      expand="lg"
      variant="dark"
      bg="dark"
      // fixed="top"
      className={styles.navbar}
    >
      <Navbar.Brand as={Link} to="/" className={styles.brand}>
        Veterinary
      </Navbar.Brand>
      <Navbar.Toggle aria-controls="responsive-navbar-nav" />
      <Navbar.Collapse id="responsive-navbar-nav">
        <Nav className="mx-auto">
          <Nav.Link as={Link} to="/cita" className={styles.navLink}>
            Cita
          </Nav.Link>
          <Nav.Link as={Link} to="/adquisicion" className={styles.navLink}>
            Adopción
          </Nav.Link>
          <Nav.Link as={Link} to="/productos" className={styles.navLink}>
            Productos
          </Nav.Link>
        </Nav>
        <Nav>
          <div className={styles.cartCont}>
            <span>{cart}</span>
            <Nav.Link
              as={Link}
              to="/carrito"
              className={styles.navLink}
              title="Carrito de Compras"
            >
              <box-icon
                name="cart"
                type="solid"
                size="md"
                color="#fff"
              ></box-icon>
            </Nav.Link>
          </div>
          {user ? null : (
            <Nav.Link
              as={Link}
              to="/login"
              className={styles.navLink}
              title="Iniciar Sesión"
            >
              <box-icon
                type="solid"
                name="user-circle"
                size="md"
                color="#fff"
              ></box-icon>
            </Nav.Link>
          )}

          {user ? (
            <Nav.Link as={Link} to="/cuenta" className={styles.navLink}>
              Mi cuenta
            </Nav.Link>
          ) : null}
          {user ? (
            <Nav.Link onClick={handleLogOut} className={styles.navLink}>
              Log out
            </Nav.Link>
          ) : null}
        </Nav>
      </Navbar.Collapse>
    </Navbar>
  );
};

export default Navigation;
