import React, { useEffect, useState } from "react";
import { Card, Container } from "react-bootstrap";
import useAuth from "../auth/useAuth";
import styles from "./AccountPage.module.css";

const AccountPage = () => {
  const { user } = useAuth();
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    // console.log(json);
    setAppointment(json);
    setHasLoaded(true);
    // console.log(json);
  };

  useEffect(() => {
    fetchAppointments();
    // console.log("jjjjjjjj");
  }, []);

  return (
    <div className="container-fluid">
      {/* <h1>AccountPage</h1>
      <h1>{user}</h1> */}
      <Container className={styles.containerflex}>
        <nav
          id="sidebarMenu"
          class="col-md-3 col-lg-2 d-md-block bg-light sidebar collapse"
        >
          <div class="sidebar-sticky pt-3">
            <ul class="nav flex-column">
              <li class="nav-item">
                <a class="nav-link active" href="#">
                  <span data-feather="home"></span>
                  Dashboard <span class="sr-only">(current)</span>
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="file"></span>
                  Orders
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="shopping-cart"></span>
                  Products
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="users"></span>
                  Customers
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="bar-chart-2"></span>
                  Reports
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="layers"></span>
                  Integrations
                </a>
              </li>
            </ul>

            <h6 class="sidebar-heading d-flex justify-content-between align-items-center px-3 mt-4 mb-1 text-muted">
              <span>Saved reports</span>
              <a
                class="d-flex align-items-center text-muted"
                href="#"
                aria-label="Add a new report"
              >
                <span data-feather="plus-circle"></span>
              </a>
            </h6>
            <ul class="nav flex-column mb-2">
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="file-text"></span>
                  Current month
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="file-text"></span>
                  Last quarter
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="file-text"></span>
                  Social engagement
                </a>
              </li>
              <li class="nav-item">
                <a class="nav-link" href="#">
                  <span data-feather="file-text"></span>
                  Year-end sale
                </a>
              </li>
            </ul>
          </div>
        </nav>
        <Container className="">
          {hasLoaded ? (
            Object.keys(appointment).map((i, cont) => {
              return (
                <Card key={appointment[i].appointment_id}>
                  <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
                  <Card.Body>
                    <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                    <Card.Text>{appointment[i].descripcion}</Card.Text>
                  </Card.Body>
                </Card>
              );
            })
          ) : (
            <p>asdasd</p>
          )}
        </Container>
      </Container>

      {/* {Object.keys(appointment).map((appoint) => (
        <Card>
          <Card.Header>Featured</Card.Header>
          <Card.Body>
            <Card.Title>Special title treatment</Card.Title>
            <Card.Text>
              With supporting text below as a natural lead-in to additional
              content.
            </Card.Text>
          </Card.Body>
        </Card>
      ))} */}
    </div>
  );
};

export default AccountPage;
