// import { useState } from "react";
import { useEffect, useState } from "react";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import { APICitas } from "../api/api";
import useAuth from "../auth/useAuth";

const Alerts = (message, variant) => {
  return <Alert variant={variant}>{message}</Alert>;
};

const CustomModal = ({ show, handleClose, appointments, pickAppointment }) => {
  const [dates, setDates] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [chosenAppointment, setChosenAppointment] = useState();

  const [formData, setFormData] = useState({
    fecha: pickAppointment.fecha,
    hora: pickAppointment.hora,
    desc: pickAppointment.descripcion,
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };

  // const getChosenAppointment = () => {
  //   Object.keys(appointments).forEach((item) => {
  //     if(appointments[item]["appointment_id"] === pickAppointment.appointment_id) {
  //       setChosenAppointment()
  //     }
  //   })
  // }

  useEffect(() => {
    console.log(pickAppointment.appointment_date.split(" ")[(1, 2)]);
    APICitas.getDates(setDates, setHasLoaded);
  }, []);

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formFecha">
            <Form.Label>Fecha</Form.Label>
            <Form.Control
              value={formData.fecha}
              onChange={handleChange}
              name="fecha"
              type="date"
              placeholder="Fecha"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Label>Hora de la Cita</Form.Label>
            <Form.Control
              as="select"
              name="hora"
              // className={styles.input}
              value={formData.hora}
              onChange={handleChange}
            >
              <option value={-1}>Seleccione la hora</option>
              {hasLoaded ? (
                Object.keys(dates).map((key) => (
                  <option key={key}>
                    {dates[key]
                      .split(" ")
                      .slice(1)
                      .toString()
                      .replace(",", " ")}
                  </option>
                ))
              ) : (
                <option>Loading...</option>
              )}
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDesc">
            <Form.Label>Desccripción</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Descripción"
              name="desc"
              value={formData.desc}
              onChange={handleChange}
            />
          </Form.Group>
          {/* <Button variant="primary" type="submit">
            Submit
          </Button> */}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleClose}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { Alerts, CustomModal };
