import React, { useEffect, useState } from "react";
import { Container, Table } from "react-bootstrap";
import {
  Alerts,
  ModalDeleteProduct,
  ModalUpdate,
} from "../../../../../components/FormUtilities";
import styles from "./GestionUsers.module.css";

const GestionUsuarios = () => {
  const [users, setUsers] = useState();

  const [show, setShow] = useState(false);
  const [selectedUser, setSelectedUser] = useState();
  const [deleteUser, setDeleteUser] = useState(false);

  const [alert, setAlert] = useState();
  const [alertMsg, setAlertMsg] = useState();
  const [alertVariant, setAlertVariant] = useState();

  const handleShow = (user) => {
    setShow(!show);
    setSelectedUser(user);
  };

  const handleDelete = (user) => {
    setDeleteUser(!deleteUser);
    setSelectedUser(user);
  };

  const getUsers = async () => {
    const res = await fetch("/users/getUser");
    const json = await res.json();
    setUsers(json);
  };

  useEffect(() => {
    getUsers();
  }, [show, deleteUser]);

  return (
    <Container fluid className={styles.container}>
      <h1 className="text-center pt-4">Gestión de Usuarios</h1>
      <div className={styles.tableCont}>
        {alert ? Alerts(alertMsg, alertVariant, setAlert) : null}
        <Table striped className="" responsive>
          <thead className={styles.tableHead}>
            <tr className="text-center">
              <th>ID</th>
              <th>Email</th>
              <th>Nombre</th>
              <th>Apellidos</th>
              <th>Teléfono</th>
              <th>Status</th>
              <th>Modificar</th>
            </tr>
          </thead>
          <tbody>
            {products ? (
              Object.keys(products).map((item) => {
                return (
                  <tr className="text-center" key={products[item].product_id}>
                    <td>{products[item].product_id}</td>
                    <td>{products[item].name}</td>
                    <td>{products[item].marca}</td>
                    <td>{products[item].price}</td>
                    <td>{products[item].cantidad}</td>
                    <td>{products[item].descripcion}</td>
                    <td>
                      <div className={styles.btnContainer}>
                        <div
                          className={styles.editIcon}
                          onClick={() => handleShow(products[item])}
                        >
                          <i className="bx bxs-edit"></i>
                        </div>
                        <div
                          className={styles.deleteIcon}
                          onClick={() => handleDelete(products[item])}
                        >
                          <i className="bx bxs-trash-alt"></i>
                        </div>
                      </div>
                    </td>
                  </tr>
                );
              })
            ) : (
              <tr>
                <td>Loading</td>
              </tr>
            )}
            {show ? (
              <ModalUpdate
                show={show}
                handleClose={handleShow}
                selectedProduct={selectedProduct}
                setAlert={setAlert}
                setAlertMsg={setAlertMsg}
                setAlertVariant={setAlertVariant}
              />
            ) : null}
            {deleteProduct ? (
              <ModalDeleteProduct
                show={deleteProduct}
                handleClose={handleDelete}
                selectedProduct={selectedProduct}
                setAlert={setAlert}
                setAlertMsg={setAlertMsg}
                setAlertVariant={setAlertVariant}
              />
            ) : null}
          </tbody>
        </Table>
      </div>
    </Container>
  );
};

export default GestionUsuarios;
