import React, { useEffect, useLayoutEffect, useState } from "react";
import {
  Button,
  Card,
  Collapse,
  Container,
  Nav,
  Navbar,
  NavDropdown,
  Row,
} from "react-bootstrap";
import useAuth from "../auth/useAuth";
import styles from "./AccountPage.module.css";

const AccountPage = () => {
  const { user } = useAuth();
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [open, setOpen] = useState(false);

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    // console.log(json);
    setAppointment(json);
    setHasLoaded(true);
    // console.log(json);
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  return (
    <>
      <div className="navbar">
        <Link to="#" className="menu-bars">
          <FaIcons.FaBars onClick={showSidebar} />
        </Link>
      </div>
      <nav className={sidebar ? "nav-menu active" : "nav-menu"}>
        <ul className="nav-menu-items" onClick={showSidebar}>
          <li className="navbar-toggle">
            <Link to="#" className="menu-bars">
              <AiIcons.AiOutlineClose />
            </Link>
          </li>
          {SidebarData.map((item, index) => {
            return (
              <li key={index} className={item.cName}>
                <Link to={item.path}>
                  {item.icon}
                  <span>{item.title}</span>
                </Link>
              </li>
            );
          })}
        </ul>
      </nav>
      <div>
        {hasLoaded ? (
          Object.keys(appointment).map((i, cont) => {
            return (
              <Card key={appointment[i].appointment_id}>
                <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
                <Card.Body>
                  <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                  <Card.Text>{appointment[i].descripcion}</Card.Text>
                </Card.Body>
              </Card>
            );
          })
        ) : (
          <p>asdasd</p>
        )}
      </div>
    </>
  );
};

export default AccountPage;
