import React, { useEffect, useState } from "react";
import {
  Button,
  Card,
  Collapse,
  Container,
  Nav,
  Navbar,
  NavDropdown,
  Row,
} from "react-bootstrap";
import useAuth from "../auth/useAuth";
import styles from "./AccountPage.module.css";

const AccountPage = () => {
  const { user } = useAuth();
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [open, setOpen] = useState(false);

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    // console.log(json);
    setAppointment(json);
    setHasLoaded(true);
    // console.log(json);
  };

  useEffect(() => {
    fetchAppointments();
    // console.log("jjjjjjjj");
  }, []);

  return (
    <div className={`${styles.wrapper} fixed-left`}>
      {hasLoaded ? (
        Object.keys(appointment).map((i, cont) => {
          return (
            <Card key={appointment[i].appointment_id}>
              <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
              <Card.Body>
                <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                <Card.Text>{appointment[i].descripcion}</Card.Text>
              </Card.Body>
            </Card>
          );
        })
      ) : (
        <p>asdasd</p>
      )}
    </div>
  );
};

export default AccountPage;
