// import { useState } from "react";
import { useEffect, useState } from "react";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import { APICitas } from "../api/api";
import useAuth from "../auth/useAuth";

const Alerts = (message, variant) => {
  return <Alert variant={variant}>{message}</Alert>;
};

const CustomModal = ({ show, handleClose, pickAppointment }) => {
  const [dates, setDates] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [userAppointment, setUserAppointment] = useState();
  const [dbAppointments, setDBAppointments] = useState();

  const [appointment, setAppointment] = useState({
    fecha: "",
    hora: "",
    desc: "",
  });

  const handleChange = (e) => {
    setAppointment({
      ...appointment,
      [e.target.name]: e.target.value,
    });
  };

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    console.log("----");
    console.log(json);

    // if (dbAppointments) setDBAppointments(json);
    // console.log(dbAppointments);

    setDBAppointments((prevState) => [...prevState, json]);
    console.log(dbAppointments);

    // console.log(dbAppointments);
    // Object.keys(json).forEach((appoint) => {
    //   if (json[appoint]["appointment_id"] === pickAppointment.appointment_id) {
    //     setUserAppointment(json[appoint]);
    //   }
    // });
    // console.log(userAppointment);
  };

  useEffect(() => {
    fetchAppointments();
    APICitas.getDates(setDates, setHasLoaded);
  }, []);

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formFecha">
            <Form.Label>Fecha</Form.Label>
            <Form.Control
              value={appointment.fecha}
              onChange={handleChange}
              name="fecha"
              type="date"
              placeholder="Fecha"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Label>Hora de la Cita</Form.Label>
            <Form.Control
              as="select"
              name="hora"
              // className={styles.input}
              value={appointment.hora}
              onChange={handleChange}
            >
              <option value={-1}>Seleccione la hora</option>
              {hasLoaded ? (
                Object.keys(dates).map((key) => (
                  <option key={key}>
                    {dates[key]
                      .split(" ")
                      .slice(1)
                      .toString()
                      .replace(",", " ")}
                  </option>
                ))
              ) : (
                <option>Loading...</option>
              )}
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDesc">
            <Form.Label>Desccripción</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Descripción"
              name="desc"
              value={appointment.desc}
              onChange={handleChange}
            />
          </Form.Group>
          {/* <Button variant="primary" type="submit">
            Submit
          </Button> */}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleClose}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { Alerts, CustomModal };
