import { APIUsers } from "../../../../../api/api";
import { Alert, Button, Form, Modal } from "react-bootstrap";

const ModalDeleteUser = ({
  show,
  handleClose,
  selectedUser,
  setAlert,
  setAlertMsg,
  setAlertVariant,
}) => {
  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    console.log(res);
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert("Usuario eliminado correctamente.", "success");
    }
  };

  const handleDelete = async () => {
    const res = await APIUsers.deleteUser({
      user: selectedUser,
    });
    checkResponse(res);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Eliminar Usuario</Modal.Title>
      </Modal.Header>
      <Modal.Body>{`¿Está seguro que desea eliminar el usuario: ${selectedUser.first_name} ${selectedUser.last_name}?`}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={handleDelete}>
          Eliminar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { ModalDeleteUser };
