import React, { useEffect, useState } from "react";
import { Card, Container } from "react-bootstrap";
import { Link } from "react-router-dom";
import useAuth from "../../auth/useAuth";
import styles from "./AccountPage.module.css";
// import Navi from "./Navi";

const AccountPage = () => {
  const { user } = useAuth();
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();
  const [sidebar, setSidebar] = useState(true);

  const showSidebar = () => setSidebar(!sidebar);

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    // console.log(json);
    setAppointment(json);
    setHasLoaded(true);
    // console.log(json);
  };

  useEffect(() => {
    fetchAppointments();
  }, []);

  return (
    <Container fluid>
      <div className={styles.navbar}>
        <Link to="#" className={styles.menuBars}>
          <box-icon
            name="chevron-right-circle"
            type="solid"
            color="#ffffff"
            size="md"
            onClick={showSidebar}
          ></box-icon>
        </Link>
        <div className={styles.info}>{user}</div>
      </div>
      <nav
        className={
          sidebar ? `${styles.navMenu} ${styles.active}` : styles.navMenu
        }
      >
        <ul className={styles.navMenuItems} onClick={showSidebar}>
          <li className={styles.navbarToggle}>
            <Link to="#" className={styles.menuBars}>
              <box-icon
                name="chevron-left-circle"
                type="solid"
                size="md"
                color="#ffffff"
              ></box-icon>
            </Link>
          </li>
          <li className={styles.navText}>Citas</li>
          <li className={styles.navText}>Pedidos</li>
        </ul>
      </nav>
      <Container fluid>
        <div
          className={
            sidebar
              ? `col-md-8 ml-sm-auto col-lg-9 col-xl-10 px-md-4 ${styles.infoContainer}`
              : styles.infoContainer
          }
        >
          {hasLoaded ? (
            Object.keys(appointment).map((i, cont) => {
              return (
                <Card key={appointment[i].appointment_id}>
                  <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
                  <Card.Body>
                    <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                    <Card.Text>{appointment[i].descripcion}</Card.Text>
                  </Card.Body>
                </Card>
              );
            })
          ) : (
            <p>asdasd</p>
          )}
        </div>
      </Container>
    </Container>
  );
};

export default AccountPage;
