import React, { useState } from "react";
import { Button, Col, Container, Form, Row } from "react-bootstrap";
import { APIUsers } from "../../api/api";
import { Alerts } from "../../components/FormUtilities";
import styles from "./RegisterPage.module.css";

const RegisterPage = () => {
  const [user, setUser] = useState({
    nombre: "",
    apellidos: "",
    email: "",
    telefono: "",
    contraseña: "",
    confirmarContraseña: "",
  });

  const [alert, setAlert] = useState();
  const [alertMsg, setAlertMsg] = useState();
  const [alertVariant, setAlertVariant] = useState();

  const [validated, setValidated] = useState(false);
  const [name, setName] = useState(true);

  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
  };

  const handleChange = (e) => {
    setUser({
      ...user,
      [e.target.name]: e.target.value,
    });
  };

  const verifyPassword = () => {
    for (const info in user) {
      if (user[info] === undefined || user[info] === "") {
        return false;
      }
    }
    return true;
  };

  const handleSubmit = async (e) => {
    // if (verifyPassword()) {
    e.preventDefault();
    const form = e.currentTarget;
    if (user.nombre !== "hola") {
      setName(true);
      console.log("jahsjkdhasjdhasdj");
    } else {
      setName(false);
    }
    // if (form.checkValidity() === false) {
    //   // console.log("kjhjkhjk");
    //   e.preventDefault();
    //   e.stopPropagation();
    // }
    // setValidated(true);
    // const res = await APIUsers.createUser({
    //   nombre: user.nombre,
    //   apellidos: user.apellidos,
    //   email: user.email,
    //   telefono: user.telefono,
    //   contraseña: user.contraseña,
    //   confirmarContraseña: user.confirmarContraseña,
    // });
    // console.log(res);
    // } else {
    // handleAlert()
    // handleAlert("Verifica tus datos", "danger");
    // }
  };

  return (
    <Container fluid className={`${styles.container} pt-4`}>
      <h1 className="text-center mt-4">Crear Cuenta</h1>
      <Form
        noValidate
        validated={validated}
        className={`${styles.form}`}
        onSubmit={handleSubmit}
      >
        {alert ? Alerts(alertMsg, alertVariant, setAlert) : null}
        <h2 className={styles.sectionSubtitle}>Datos Personales</h2>
        <Row>
          <Col className="col-md-6 col-12">
            <Form.Group controlId="formNombre">
              <Form.Label>Nombre</Form.Label>
              <Form.Control
                type="text"
                name="nombre"
                placeholder="Ingresa tu nombre"
                className={styles.input}
                value={user.nombre}
                onChange={handleChange}
                // required
                isValid={name}
              />
              <Form.Control.Feedback type="invalid">
                Please choose a username.
              </Form.Control.Feedback>
            </Form.Group>
          </Col>
          <Col className="col-md-6 col-12">
            <Form.Group controlId="formApellidos">
              <Form.Label>Apellidos</Form.Label>
              <Form.Control
                type="text"
                name="apellidos"
                placeholder="Ingresa tus apellidos"
                className={styles.input}
                value={user.apellidos}
                onChange={handleChange}
                required
                isValid={false}
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col className="col-12 col-md-6">
            <Form.Group controlId="formEmail">
              <Form.Label>Correo electrónico</Form.Label>
              <Form.Control
                type="text"
                name="email"
                placeholder="Ingresa tu correo electrónico"
                className={styles.input}
                value={user.email}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formTelefono">
              <Form.Label>Número de Teléfono</Form.Label>
              <Form.Control
                type="text"
                name="telefono"
                placeholder="Ingresa tu número telefonico"
                className={styles.input}
                value={user.telefono}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col className="col-12 col-md-6">
            <Form.Group controlId="formContraseña">
              <Form.Label>Contraseña</Form.Label>
              <Form.Control
                type="password"
                name="contraseña"
                placeholder="Contraseña"
                className={styles.input}
                value={user.contraseña}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
          <Col>
            <Form.Group controlId="formConfirmarContraseña">
              <Form.Label>Confirmar Contraseña</Form.Label>
              <Form.Control
                type="password"
                name="confirmarContraseña"
                placeholder="Confirmar contraseña"
                className={styles.input}
                value={user.confirmarContraseña}
                onChange={handleChange}
                required
              />
            </Form.Group>
          </Col>
        </Row>
        <Row>
          <Col className="d-flex justify-content-end mt-3">
            <Button variant="danger" className="mr-3" size="lg">
              Cancelar
            </Button>
            <Button type="submit" variant="primary" size="lg">
              Registrar
            </Button>
          </Col>
        </Row>
      </Form>
    </Container>
  );
};

export default RegisterPage;
