import React, { useEffect, useState } from "react";
import { Button, Container, Form } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import { APIUsers } from "../../api/api";
import useAuth from "../../auth/useAuth";
import { Alerts } from "../../components/FormUtilities";
import styles from "./Login.module.css";

const LoginPage = () => {
  const { user, setUser } = useAuth();
  const [form, setForm] = useState({});
  const [showAlert, setShowAlert] = useState();
  const history = useHistory();

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleAlert = () => {
    setShowAlert(true);
    setTimeout(() => {
      setShowAlert(false);
    }, 3000);
  };

  const getUser = async (response) => {
    if (response.status === 200) {
      const json = await response.json();
      setUser(json.email);
    } else {
      handleAlert();
    }
  };

  useEffect(() => {
    console.log("jjjj");
    const redirect = () => {
      history.push("/cuenta");
    };
    if (user) redirect();
  }, [history, user]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await APIUsers.login({
      user: form["email"],
      password: form["password"],
    });
    getUser(response);
  };

  return (
    <Container className={styles.container}>
      <h1 className={styles.title}>Iniciar Sesión</h1>
      <Form className={styles.form}>
        {showAlert
          ? Alerts("Usuario o contrasñea incorrectos.", "danger")
          : null}
        <Form.Group controlId="formEmail">
          <Form.Label>Correo Electrónico</Form.Label>
          <Form.Control type="email" name="email" onChange={handleChange} />
        </Form.Group>
        <Form.Group controlId="formPassword">
          <Form.Label>Contraseña</Form.Label>
          <Form.Control
            type="password"
            name="password"
            onChange={handleChange}
          />
        </Form.Group>
        <Button
          variant="success"
          className={styles.submitBtn}
          onClick={handleSubmit}
        >
          Iniciar Sesión
        </Button>
      </Form>
      <Container className={styles.redirect}>
        ¿Aún no tienes cuenta? <Link to="/registro">Registrate</Link>
      </Container>
    </Container>
  );
};

export default LoginPage;
