import { APIUsers } from "../../../../../api/api";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import { useState } from "react";

const ModalDeleteUser = ({
  show,
  handleClose,
  selectedUser,
  setAlert,
  setAlertMsg,
  setAlertVariant,
}) => {
  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    console.log(res);
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert("Usuario eliminado correctamente.", "success");
    }
  };

  const handleDelete = async () => {
    const res = await APIUsers.deleteUser({
      user: selectedUser,
    });
    checkResponse(res);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Eliminar Usuario</Modal.Title>
      </Modal.Header>
      <Modal.Body>{`¿Está seguro que desea eliminar el usuario: ${selectedUser.first_name} ${selectedUser.last_name}?`}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={handleDelete}>
          Eliminar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

const ModalUpdateUser = ({
  show,
  handleClose,
  selectedUser,
  setAlert,
  setAlertMsg,
  setAlertVariant,
}) => {
  const [formUser, setFormUser] = useState({
    email: selectedUser.email,
    first_name: selectedUser.first_name,
    last_name: selectedUser.last_name,
    phone: selectedUser.phone,
    status: selectedUser.status,
  });

  const handleChange = (e) => {
    setFormUser({
      ...formUser,
      [e.target.name]: e.target.value,
    });
  };

  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert("Usuario modificado correctamente.", "success");
    }
  };

  const updateProduct = async () => {
    const res = await APIUsers.updateUser({
      id: selectedUser.user_id,
      name: formUser.first_name,
      lastName: formUser.last_name,
      email: formUser.email,
      phone: formUser.phone,
      status: formUser.status,
    });
    checkResponse(res);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar Producto</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formNombre">
            <Form.Label>Nombre del Producto</Form.Label>
            <Form.Control
              value={formProduct.name}
              onChange={handleChange}
              name="name"
              type="text"
              placeholder="Nombre del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formMarca">
            <Form.Label>Marca del Producto</Form.Label>
            <Form.Control
              value={formProduct.marca}
              onChange={handleChange}
              name="marca"
              type="text"
              placeholder="Marca del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formPrice">
            <Form.Label>Precio del Producto</Form.Label>
            <Form.Control
              value={formProduct.price}
              onChange={handleChange}
              name="price"
              type="text"
              placeholder="Precio del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formCantidad">
            <Form.Label>Stock del Producto</Form.Label>
            <Form.Control
              value={formProduct.cantidad}
              onChange={handleChange}
              name="cantidad"
              type="text"
              placeholder="Stock del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDesc">
            <Form.Label>Descripción</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Descripción"
              name="descripcion"
              value={formProduct.descripcion}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formImagen">
            <Form.Label>Imágen del Producto</Form.Label>
            <Form.File
              custom
              label={fileName}
              data-browse="Seleccionar"
              onChange={handleFileChange}
              accept="image/*"
            ></Form.File>
            <img
              className="img-fluid mt-2"
              src={`${selectedFile}`}
              alt="product-view"
            ></img>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={updateProduct}>
          Editar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { ModalDeleteUser, ModalUpdateUser };
