// import { useState } from "react";
import { useEffect, useRef, useState } from "react";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import { APICitas } from "../api/api";

const Alerts = (message, variant) => {
  return <Alert variant={variant}>{message}</Alert>;
};

const CustomModal = ({
  show,
  handleClose,
  appointments,
  pickAppointment,
  setAlert,
  setAlertMsg,
  setAlertVariant,
}) => {
  const [dates, setDates] = useState();
  const [hasLoaded, setHasLoaded] = useState(false);
  const isMountedRef = useRef(true);

  const [formData, setFormData] = useState({
    fecha: pickAppointment.appointment_date.split(" ")[0],
    hora: pickAppointment.appointment_date
      .split(" ")
      .slice(1)
      .toString()
      .replace(",", " "),
    desc: pickAppointment.descripcion,
  });

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value,
    });
  };
  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    console.log(res);
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert("Cita modificada correctamente.", "success");
    }
  };

  const updateAppointment = async () => {
    const res = await APICitas.updateCita({
      fecha: formData.fecha,
      hora: formData.hora,
      descripcion: formData.desc,
      oldAppointment: pickAppointment,
    });
    checkResponse(res);
  };

  const getCitas = async () => {
    const res = await APICitas.getDates();
    console.log(res);
    if (isMountedRef.current) {
      setHasLoaded(true);
      setDates(res);
      console.log("ahsdjka");
    }
  };

  useEffect(() => {
    getCitas();
    return () => {
      setDates({});
      setHasLoaded(false);
      isMountedRef.current = false;
    };
  }, []);

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formFecha">
            <Form.Label>Fecha</Form.Label>
            <Form.Control
              value={formData.fecha}
              onChange={handleChange}
              name="fecha"
              type="date"
              placeholder="Fecha"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Label>Hora de la Cita</Form.Label>
            <Form.Control
              as="select"
              name="hora"
              value={formData.hora}
              onChange={handleChange}
            >
              <option value={-1}>{formData.hora}</option>
              {hasLoaded ? (
                Object.keys(dates).map((key) => (
                  <option key={key}>
                    {dates[key]
                      .split(" ")
                      .slice(1)
                      .toString()
                      .replace(",", " ")}
                  </option>
                ))
              ) : (
                <option>Loading...</option>
              )}
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDesc">
            <Form.Label>Desccripción</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Descripción"
              name="desc"
              value={formData.desc}
              onChange={handleChange}
            />
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={updateAppointment}>
          Editar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

const ModalDelete = ({
  show,
  handleClose,
  pickAppointment,
  setAlert,
  setAlertMsg,
  setAlertVariant,
}) => {
  const handleDelete = async () => {
    // console.log(pickAppointment);
    await APICitas.deleteCita({
      appointment: pickAppointment,
    });
    handleClose();
    // console.log(res);
    // const { appointment_date } = pickAppointment;
    // console.log(appointment_date);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Eliminar Cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>{`¿Está seguro que desea eliminar la cita con fecha: ${
        pickAppointment.appointment_date.split(" ")[0]
      } y hora: ${pickAppointment.appointment_date
        .split(" ")
        .slice(1)
        .toString()
        .replace(",", " ")}?`}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleDelete}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { Alerts, CustomModal, ModalDelete };
