import { useState } from "react";
import { Button, Form, Modal } from "react-bootstrap";

const ModalUpdate = ({
  show,
  handleClose,
  selectedProduct,
  setAlert,
  setAlertMsg,
  setAlertVariant,
}) => {
  const [formProduct, setFormProduct] = useState({
    name: selectedProduct.name,
    cantidad: selectedProduct.cantidad,
    price: selectedProduct.price,
    descripcion: selectedProduct.descripcion,
    marca: selectedProduct.marca,
  });

  const [fileName, setFileName] = useState(selectedProduct.img);
  const [selectedFile, setSelectedFile] = useState(
    `${IMG_URL}${selectedProduct.img}`
  );

  const handleChange = (e) => {
    setFormProduct({
      ...formProduct,
      [e.target.name]: e.target.value,
    });
  };

  const handleFileChange = (e) => {
    const [file] = e.target.files;
    setFileName(file.name);

    const reader = new FileReader();
    reader.onloadend = () => {
      setSelectedFile(reader.result);
    };
    reader.readAsDataURL(file);
  };

  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert("Cita modificada correctamente.", "success");
    }
  };

  const updateProduct = async () => {
    const res = await APIProducts.updateProduct({
      id: selectedProduct.product_id,
      name: formProduct.name,
      cantidad: formProduct.cantidad,
      price: formProduct.price,
      descripcion: formProduct.descripcion,
      marca: formProduct.marca,
      imgName: fileName,
      img: selectedFile,
    });
    checkResponse(res);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar Producto</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formNombre">
            <Form.Label>Nombre del Producto</Form.Label>
            <Form.Control
              value={formProduct.name}
              onChange={handleChange}
              name="name"
              type="text"
              placeholder="Nombre del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formMarca">
            <Form.Label>Marca del Producto</Form.Label>
            <Form.Control
              value={formProduct.marca}
              onChange={handleChange}
              name="marca"
              type="text"
              placeholder="Marca del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formPrice">
            <Form.Label>Precio del Producto</Form.Label>
            <Form.Control
              value={formProduct.price}
              onChange={handleChange}
              name="price"
              type="text"
              placeholder="Precio del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formCantidad">
            <Form.Label>Stock del Producto</Form.Label>
            <Form.Control
              value={formProduct.cantidad}
              onChange={handleChange}
              name="cantidad"
              type="text"
              placeholder="Stock del Producto"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDesc">
            <Form.Label>Descripción</Form.Label>
            <Form.Control
              as="textarea"
              placeholder="Descripción"
              name="descripcion"
              value={formProduct.descripcion}
              onChange={handleChange}
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formImagen">
            <Form.Label>Imágen del Producto</Form.Label>
            <Form.File
              custom
              label={fileName}
              data-browse="Seleccionar"
              onChange={handleFileChange}
              accept="image/*"
            ></Form.File>
            <img
              className="img-fluid mt-2"
              src={`${selectedFile}`}
              alt="product-view"
            ></img>
          </Form.Group>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={updateProduct}>
          Editar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { ModalUpdate };
