// import { useState } from "react";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import useAuth from "../auth/useAuth";

const Alerts = (message, variant) => {
  return <Alert variant={variant}>{message}</Alert>;
};

const CustomModal = ({ show, handleClose, userAppointment }) => {
  const { user } = useAuth();

  const [appointment, setAppointment] = {
    fecha: "",
    hora: "",
    descripcion: "",
  };

  const handleChange = (e) => {
    setAppointment({
      ...appointment,
      [e.target.name]: e.target.value,
    });
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formBasicEmail">
            <Form.Label>Email address</Form.Label>
            <Form.Control type="email" placeholder="Enter email" />
          </Form.Group>

          <Form.Group className="mb-3" controlId="formBasicPassword">
            <Form.Label>Password</Form.Label>
            <Form.Control type="password" placeholder="Password" />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Label>Hora de la Cita</Form.Label>
            <Form.Control
              as="select"
              name="hora"
              className={styles.input}
              value={appointment.hora}
              onChange={handleChange}
            >
              <option value={-1}>Seleccione la hora</option>
              {hasLoaded ? (
                Object.keys(dates).map((key) => (
                  <option key={key}>
                    {dates[key]
                      .split(" ")
                      .slice(1)
                      .toString()
                      .replace(",", " ")}
                  </option>
                ))
              ) : (
                <option>Loading...</option>
              )}
            </Form.Control>
          </Form.Group>
          <Button variant="primary" type="submit">
            Submit
          </Button>
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleClose}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { Alerts, CustomModal };
