import React, { useState } from "react";
import { Container } from "react-bootstrap";
import Switch from "react-bootstrap/esm/Switch";
import { Link, Route, useRouteMatch } from "react-router-dom";
import useAuth from "../../auth/useAuth";
import AccountPage from "./AccountPage";
import styles from "./AccountPage.module.css";

const Sidebar = () => {
  const { user } = useAuth();
  const [sidebar, setSidebar] = useState(true);
  const showSidebar = () => setSidebar(!sidebar);
  let { path, url } = useRouteMatch();
  // const [userPage, setUserPa]

  return (
    <Container fluid className={`${styles.container} p-0`}>
      <div className={styles.navbar}>
        <Link to="#" className={styles.menuBars}>
          <box-icon
            name="chevron-right-circle"
            type="solid"
            size="md"
            onClick={showSidebar}
          ></box-icon>
        </Link>
        <div className={styles.info}>{user}</div>
      </div>
      <nav
        className={
          sidebar ? `${styles.navMenu} ${styles.active}` : styles.navMenu
        }
      >
        <ul className={styles.navMenuItems} onClick={showSidebar}>
          <li className={styles.navbarToggle}>
            <Link to="#" className={styles.menuBars}>
              <box-icon
                name="chevron-left-circle"
                type="solid"
                size="md"
              ></box-icon>
            </Link>
          </li>
          <li className={styles.navText}>
            {/* <Link to="/carrito/citas" className={styles.sidebarOption}> */}
            <Link to={`${url}/citass`} className={styles.sidebarOption}>
              <box-icon name="plus-medical"></box-icon>
              <span className={styles.sidebarSpan}>Citas</span>
            </Link>
          </li>
          <li className={styles.navText}>
            <Link to="/carrito" className={styles.sidebarOption}>
              <box-icon name="cart" type="solid"></box-icon>
              <span className={styles.sidebarSpan}>Pedidos</span>
            </Link>
          </li>
          <li className={styles.navText}>
            <Link to="/carrito" className={styles.sidebarOption}>
              <box-icon type="solid" name="user"></box-icon>
              <span className={styles.sidebarSpan}>Cuenta</span>
            </Link>
          </li>
        </ul>
      </nav>

      <Switch>
        <Route exact path={path}>
          <h3>Please select a topic.</h3>
        </Route>
        <Route path={`${path}/:topicId`}>
          <AccountPage />
        </Route>
      </Switch>
      {/* <AccountPageCita></AccountPageCita> */}
      {/* <Switch>
        <Route exact path={path}>
          <h3>Please select a topic.</h3>
        </Route>
        <Route path={`${path}/:citass`}>
          <AccountPageCita />
        </Route>
      </Switch> */}
    </Container>
  );
};

export default Sidebar;
