// import { useState } from "react";
import { useEffect, useState } from "react";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import { APICitas } from "../api/api";
import useAuth from "../auth/useAuth";

const Alerts = (message, variant) => {
  return <Alert variant={variant}>{message}</Alert>;
};

const CustomModal = ({ show, handleClose, userAppointment }) => {
  const { user } = useAuth();
  const [dates, setDates] = useState();
  const [hasLoaded, setHasLoaded] = useState();

  const [appointment, setAppointment] = useState({
    fecha: "",
    hora: "",
    desc: "",
  });

  const handleChange = (e) => {
    setAppointment({
      ...appointment,
      [e.target.name]: e.target.value,
    });
  };

  useEffect(() => {
    APICitas.getDates(setDates, setHasLoaded);
  }, []);

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Editar cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Group className="mb-3" controlId="formFecha">
            <Form.Label value={appointment.fecha} onChange={handleChange}>
              Fecha
            </Form.Label>
            <Form.Control
              value={appointment.fecha}
              onChange={handleChange}
              type="date"
              placeholder="Fecha"
            />
          </Form.Group>
          <Form.Group className="mb-3" controlId="formBasicCheckbox">
            <Form.Label>Hora de la Cita</Form.Label>
            <Form.Control
              as="select"
              name="hora"
              // className={styles.input}
              value={appointment.hora}
              onChange={handleChange}
            >
              <option value={-1}>Seleccione la hora</option>
              {hasLoaded ? (
                Object.keys(dates).map((key) => (
                  <option key={key}>
                    {dates[key]
                      .split(" ")
                      .slice(1)
                      .toString()
                      .replace(",", " ")}
                  </option>
                ))
              ) : (
                <option>Loading...</option>
              )}
            </Form.Control>
          </Form.Group>
          <Form.Group className="mb-3" controlId="formDesc">
            <Form.Label>Desccripción</Form.Label>
            <Form.Control
              value={appointment.desc}
              onChange={handleChange}
              as="textarea"
              placeholder="Descripción"
            />
          </Form.Group>
          {/* <Button variant="primary" type="submit">
            Submit
          </Button> */}
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Close
        </Button>
        <Button variant="primary" onClick={handleClose}>
          Save Changes
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

export { Alerts, CustomModal };
