import React, { useEffect, useState } from "react";
import { Card, Container } from "react-bootstrap";
import useAuth from "../auth/useAuth";
import styles from "./AccountPage.module.css";

const AccountPage = () => {
  const { user } = useAuth();
  const [appointment, setAppointment] = useState();
  const [hasLoaded, setHasLoaded] = useState();

  const fetchAppointments = async () => {
    const response = await fetch("/citas/getAppointment");
    const json = await response.json();
    // console.log(json);
    setAppointment(json);
    setHasLoaded(true);
    // console.log(json);
  };

  useEffect(() => {
    fetchAppointments();
    // console.log("jjjjjjjj");
  }, []);

  return (
    <div className="container-fluid">
      {/* <h1>AccountPage</h1>
      <h1>{user}</h1> */}

      <div className={styles.layoutSidenav_content}>
        {hasLoaded ? (
          Object.keys(appointment).map((i, cont) => {
            return (
              <Card key={appointment[i].appointment_id}>
                <Card.Header>{`Cita: ${cont + 1}`}</Card.Header>
                <Card.Body>
                  <Card.Title>{`Fecha: ${appointment[i].appointment_date}`}</Card.Title>
                  <Card.Text>{appointment[i].descripcion}</Card.Text>
                </Card.Body>
              </Card>
            );
          })
        ) : (
          <p>asdasd</p>
        )}
      </div>

      {/* {Object.keys(appointment).map((appoint) => (
        <Card>
          <Card.Header>Featured</Card.Header>
          <Card.Body>
            <Card.Title>Special title treatment</Card.Title>
            <Card.Text>
              With supporting text below as a natural lead-in to additional
              content.
            </Card.Text>
          </Card.Body>
        </Card>
      ))} */}
    </div>
  );
};

export default AccountPage;
